mapper.select:
SELECT post_id AS postId, post_type AS postType, title AS title, content AS content, writer_id AS writerId, writer_type AS writerType, branch_id AS branchId, display_start AS displayStart, display_end AS displayEnd, is_visible AS isVisible, post_visible AS postVisible, created_at AS createdAt, updated_at AS updatedAt FROM community_post WHERE post_type = 'FAQ'
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND is_visible = #{visible}
AND post_visible = 1 ORDER BY post_id DESC LIMIT #{size} OFFSET #{offset}
