mapper.select:
SELECT COUNT(*)
FROM COMMUNITY_COMMENT
WHERE comment_id = #{commentId}
  AND writer_id = #{writerId}
  AND comment_visible = 1

mapper.update:
UPDATE COMMUNITY_COMMENT
SET
    content = #{content}
WHERE comment_id = #{commentId}
  AND writer_id = #{writerId}
  AND comment_visible = 1

mapper.delete:
DELETE
FROM COMMUNITY_COMMENT
WHERE comment_id = #{commentId}
  AND writer_id = #{writerId}
